(1002, 3, '100', '{\"zh-cn\":\"\\u4ea7\\u54c1\\u7f3a\\u9677\\u6570\\u636e\\u6c47\\u603b\\u8868\",\"zh-tw\":\"\",\"en\":\"\",\"de\":\"\",\"fr\":\"\"}', '', 'SELECT \n  t1.name AS  \"\", \n  IFNULL(t2.name, \'/\') AS \"\", \n  IFNULL(t3.name, \'/\') AS \"\", \n  IFNULL(t6.exfixedstorys, 0) AS \"\", \n  round(IFNULL(t6.exfixedstorysmate, 0),3) AS \"\",\n  IFNULL(t8.storycases, 0) AS \"\",\n  round(storycases/exfixedstorysmate,3) AS \"\",\n  round(IFNULL(t10.casestorys/t6.exfixedstorys,0),3) AS \"\",\n  IFNULL(t7.bug, 0) AS \"Bug\",\n  IFNULL(t7.effbugs, 0) AS \"Bug\",\n  IFNULL(t7.pri12bugs, 0) AS \"12Bug\",\n  round(bug/exfixedstorysmate,3) AS \"Bug\",\n  IFNULL(t7.fixedbugs, 0) AS \"Bug\",\n  round(t7.fixedbugs/bug,3) \"Bug\"\nFROM \n  zt_product AS t1 \n  LEFT JOIN zt_project AS t2 ON t1.program = t2.id AND t2.type = \'program\' AND t2.grade = 1 \n  LEFT JOIN zt_module AS t3 ON t1.line = t3.id AND t3.type = \'line\' \n  LEFT JOIN (\n  SELECT\n  product, \n  count(id) exfixedstorys, \n  sum(estimate) exfixedstorysmate\n  FROM zt_story \n  WHERE deleted = \'0\'  and (stage in (\'developed\',\'testing\',\'verfied\',\'released\') or (status=\'closed\' and closedReason=\'done\'))\n  GROUP BY product) AS t6 ON t1.id = t6.product \n  LEFT JOIN (SELECT product, COUNT(id)  AS bug,\n  SUM(case when  resolution in (\'fixed\',\'postponed\') or status=\'active\' then 1 else 0 end) effbugs, \n  SUM(CASE WHEN  resolution=\'fixed\' then 1 else 0 end) fixedbugs,\n  SUM(CASE WHEN severity in (1,2) then 1 else 0 end) pri12bugs\n  FROM zt_bug WHERE deleted = \'0\' GROUP BY product) AS t7 ON t1.id = t7.product \n  LEFT JOIN (SELECT product, COUNT(id) AS storycases FROM zt_case WHERE deleted=\'0\' GROUP BY product) AS t8 ON t1.id=t8.product\n  LEFT JOIN (\n   select \n   t9.product, \n   count(t9.story) casestorys\n   from(\n     SELECT \n     zt_case.product,zt_case.story \n     FROM zt_case \n     left join zt_story\n     on zt_case.story=zt_story.id\n     WHERE zt_case.deleted=\'0\' and zt_case.story !=\'0\' and zt_story.deleted=\'0\' and  (zt_story.stage in (\'developed\',\'testing\',\'verfied\',\'released\') or (zt_story.status=\'closed\' and zt_story.closedReason=\'done\'))\n     GROUP BY product, story) t9\n     group by product) t10\n     on t1.id=t10.product\nWHERE t1.deleted = \'0\' AND t1.status != \'closed\' AND t1.shadow = \'0\'AND t1.vision = \'rnd\'\nORDER BY t1.order', '{\"\\u4ea7\\u54c1\":{\"name\":\"\\u4ea7\\u54c1\",\"object\":\"story\",\"field\":\"\\u4ea7\\u54c1\",\"type\":\"string\"},\"\\u4e00\\u7ea7\\u9879\\u76ee\\u96c6\":{\"name\":\"\\u4e00\\u7ea7\\u9879\\u76ee\\u96c6\",\"object\":\"story\",\"field\":\"\\u4e00\\u7ea7\\u9879\\u76ee\\u96c6\",\"type\":\"string\"},\"\\u4ea7\\u54c1\\u7ebf\":{\"name\":\"\\u4ea7\\u54c1\\u7ebf\",\"object\":\"story\",\"field\":\"\\u4ea7\\u54c1\\u7ebf\",\"type\":\"string\"},\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u6570\":{\"name\":\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u6570\",\"object\":\"story\",\"field\":\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u6570\",\"type\":\"string\"},\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u89c4\\u6a21\\u6570\":{\"name\":\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u89c4\\u6a21\\u6570\",\"object\":\"story\",\"field\":\"\\u7814\\u53d1\\u5b8c\\u6210\\u9700\\u6c42\\u89c4\\u6a21\\u6570\",\"type\":\"number\"},\"\\u9700\\u6c42\\u7528\\u4f8b\\u6570\":{\"name\":\"\\u9700\\u6c42\\u7528\\u4f8b\\u6570\",\"object\":\"story\",\"field\":\"\\u9700\\u6c42\\u7528\\u4f8b\\u6570\",\"type\":\"string\"},\"\\u7528\\u4f8b\\u5bc6\\u5ea6\":{\"name\":\"\\u7528\\u4f8b\\u5bc6\\u5ea6\",\"object\":\"story\",\"field\":\"\\u7528\\u4f8b\\u5bc6\\u5ea6\",\"type\":\"number\"},\"\\u7528\\u4f8b\\u8986\\u76d6\\u7387\":{\"name\":\"\\u7528\\u4f8b\\u8986\\u76d6\\u7387\",\"object\":\"story\",\"field\":\"\\u7528\\u4f8b\\u8986\\u76d6\\u7387\",\"type\":\"number\"},\"Bug\\u6570\":{\"name\":\"Bug\\u6570\",\"object\":\"story\",\"field\":\"Bug\\u6570\",\"type\":\"string\"},\"\\u6709\\u6548Bug\\u6570\":