INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10114, '-', 2, 'pie', '69', '0', '', '[{\"type\":\"pie\",\"group\":[{\"field\":\"status\",\"name\":\"\\u72b6\\u6001\",\"group\":\"\"}],\"metric\":[{\"field\":\"id\",\"name\":\"id\",\"valOrAgg\":\"count\"}]}]', '[]', 0, '{\"id\":{\"name\":\"id\",\"object\":\"zt_project\",\"field\":\"id\",\"type\":\"number\"},\"name\":{\"name\":\"name\",\"object\":\"zt_project\",\"field\":\"name\",\"type\":\"string\"},\"status\":{\"name\":\"\\u72b6\\u6001\",\"object\":\"project\",\"field\":\"status\",\"type\":\"option\"},\"prograss\":{\"name\":\"prograss\",\"object\":\"task\",\"field\":\"prograss\",\"type\":\"number\"},\"planPrograss\":{\"name\":\"planPrograss\",\"object\":\"task\",\"field\":\"planPrograss\",\"type\":\"number\"},\"endYear\":{\"name\":\"endYear\",\"object\":\"task\",\"field\":\"endYear\",\"type\":\"string\"}}', '{\"id\":{\"zh-cn\":\"\\u9879\\u76eeID\",\"zh-tw\":\"\",\"en\":\"id\",\"de\":\"\",\"fr\":\"\"},\"name\":{\"zh-cn\":\"\\u9879\\u76ee\\u540d\\u79f0\",\"zh-tw\":\"\",\"en\":\"name\",\"de\":\"\",\"fr\":\"\"},\"status\":{\"zh-cn\":\"\\u72b6\\u6001\",\"zh-tw\":\"\",\"en\":\"status\",\"de\":\"\",\"fr\":\"\"},\"prograss\":{\"zh-cn\":\"\\u9879\\u76ee\\u8fdb\\u5ea6\",\"zh-tw\":\"\",\"en\":\"prograss\",\"de\":\"\",\"fr\":\"\"},\"planPrograss\":{\"zh-cn\":\"\\u8ba1\\u5212\\u8fdb\\u5ea6\",\"zh-tw\":\"\",\"en\":\"planPrograss\",\"de\":\"\",\"fr\":\"\"},\"endYear\":{\"zh-cn\":\"\\u7ed3\\u675f\\u5e74\\u4efd\",\"zh-tw\":\"\",\"en\":\"endYear\",\"de\":\"\",\"fr\":\"\"}}', 'SELECT t1.id, t1.name, \nIF(\n    DATEDIFF(t1.`end`, NOW()) < 0, \n    \"\", \n    (IF(\n        (IFNULL(prograss, 0) >= (DATEDIFF(NOW(), t1.`begin`) / DATEDIFF(t1.`end`, t1.`begin`) * 100) AND LEFT(t1.`end`, 4) != \'2059\') \n        OR LEFT(t1.`end`, 4) = \'2059\' ,\n        \"\",\n        \"\"\n    ))) AS \'status\',\nIFNULL(prograss, 0) AS prograss, ROUND(DATEDIFF(NOW(), t1.`begin`) / DATEDIFF(t1.`end`, t1.`begin`) * 100, 2)  AS planPrograss,LEFT(t1.`end`, 4) AS endYear\nFROM zt_project AS t1\nLEFT JOIN (\n    SELECT t22.project,\n    ROUND(IF(SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0)) > 0, SUM(t22.consumed) / (SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0))), 0) * 100, 2) AS prograss\n    FROM zt_project AS t21\n    LEFT JOIN zt_task AS t22 ON t21.id = t22.execution\n    WHERE t21.deleted = \'0\' AND t21.type IN (\'sprint\', \'kanban\')\n    AND t22.deleted = \'0\' AND t22.parent < 1\n    GROUP BY t22.project\n    UNION\n    SELECT  t.project, ROUND(SUM(t.prograss * (t.percent / 100)), 2) as prograss\n    FROM (\n        SELECT t21.id,t21.percent, t22.project,\n        IF(SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0)) > 0, ROUND(SUM(t22.consumed) / (SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0))) * 1000 / 1000 * 100, 2), 0)  AS prograss\n        FROM zt_project AS t21\n        LEFT JOIN zt_task AS t22 ON t21.id = t22.execution\n        WHERE t21.deleted = \'0\' AND t21.type = \'stage\'\n        AND t22.deleted = \'0\' AND t22.parent < 1\n        AND t22.id IS NOT NULL\n        GROUP BY t21.id, t21.percent, t22.project\n    ) t\n    GROUP BY t.project\n) AS t2 ON t1.id = t2.project \nWHERE t1.deleted = \'0\'\nAND t1.status = \'doing\' \nAND t1.type = \'project\'', 'published', 0, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10115, '-', 2, 'pie', '69', '0', '', '[{\"type\":\"pie\",\"group\":[{\"field\":\"status\",\"name\":\"\\u72b6\\u6001\",\"group\":\"\"}],\"metric\":[{\"field\":\"id\",\"name\":\"\\u9879\\u76eeID\",\"valOrAgg\":\"count\"}]}]', '[]', 0, '{\"id\":{\"name\":\"\\u9879\\u76eeID\",\"object\":\"project\",\"field\":\"id\",\"type\":\"number\"},\"name\":{\"name\":\"\\u9879\\u76ee\\u540d\\u79f0\",\"object\":\"project\",\"field\":\"name\",\"type\":\"string\"},\"status\":{\"name\":\"\\u72b6\\u6001\",\"object\":\"project\",\"field\":\"status\",\"type\":\"option\"},\"prograss\":{\"name\":\"prograss\",\"object\":\"task\",\"field\":\"prograss\",\"type\":\"number\"},\"planPrograss\":{\"name\":\"planPrograss\",\"object\":\"task\",\"field\":\"planPrograss\",\"type\":\"number\"},\"end\":{\"name\":\"\\u8ba1\\u5212\\u5b8c\\u6210\",\"object\":\"project\",\"field\":\"end\",\"type\":\"date\"}}', '{\"id\":{\"zh-cn\":\"\\u9879\\u76eeID\",\"zh-tw\":\"\",\"en\":\"id\",\"de\":\"\",\"fr\":\"\"},\"name\":{\"zh-cn\":\"\\u9879\\u76ee\\u540d\\u79f0\",\"zh-tw\":\"\",\"en\":\"name\",\"de\":\"\",\"fr\":\"\"},\"status\":{\"zh-cn\":\"\\u72b6\\u6001\",\"zh-tw\":\"\",\"en\":\"status\",\"de\":\"\",\"fr\":\"\"},\"prograss\":{\"zh-cn\":\"\\u9879\\u76ee\\u8fdb\\u5ea6\",\"zh-tw\":\"\",\"en\":\"prograss\",\"de\":\"\",\"fr\":\"\"},\"planPrograss\":{\"zh-cn\":\"\\u8ba1\\u5212\\u8fdb\\u5ea6\",\"zh-tw\":\"\",\"en\":\"planPrograss\",\"de\":\"\",\"fr\":\"\"},\"end\":{\"zh-cn\":\"\\u8ba1\\u5212\\u5b8c\\u6210\",\"zh-tw\":\"\",\"en\":\"end\",\"de\":\"\",\"fr\":\"\"}}', 'SELECT id, name,IF(\n    DATEDIFF(`end`, NOW()) < 0,\n    \"\",\n    (IF(\n        prograss >= planPrograss,\n        \"\",\n        \"\"\n    ))\n) AS status,\nprograss, planPrograss, `end`\nFROM (\nSELECT t1.id,t1.name,ROUND(DATEDIFF(NOW(), t1.`begin`) / DATEDIFF(t1.`end`, t1.`begin`) * 100, 2) AS planPrograss,t1.`end`,\nROUND(IF(SUM(t2.consumed) + SUM(IF(t2.status != \'closed\' && t2.status != \'cancel\', t2.`left`, 0)) > 0, SUM(t2.consumed) / (SUM(t2.consumed) + SUM(IF(t2.status != \'closed\' && t2.status != \'cancel\', t2.`left`, 0))), 0) * 100, 2) AS prograss\nFROM zt_project AS t1\nLEFT JOIN zt_task AS t2 ON t1.id = t2.execution\nWHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status = \'doing\' AND t1.multiple = \'1\'\nAND ((t2.deleted = \'0\' AND t2.parent < 1) OR t2.id IS NULL)\nGROUP BY t1.id\n) AS t', 'published', 0, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10116, '-', 2, 'table', '84', '0', '', '{\"group\":[],\"column\":[{\"field\":\"program\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"begin\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"end\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"planDuration\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"realBegan\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"realDuration\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"prograss\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"status\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, '', '', 'SELECT t1.id, t1.name, IFNULL(t3.name, \'/\') AS program,t1.`begin`, IF(YEAR(t1.`end`) = \'2059\', \"\", t1.`end`) AS `end`, IF(YEAR(t1.`end`) = \'2059\', \"\", DATEDIFF(t1.`end`, t1.`begin`) + 1) AS planDuration,\r\nIF(LEFT(t1.realBegan, 4) = \'0000\', \'/\', t1.realBegan) AS realBegan, IF(YEAR(t1.`end`) = \'2059\', \"\", IF(DATEDIFF(t1.`end`, NOW()) >= 0, DATEDIFF(t1.`end`, NOW()) + 1, 0)) AS realDuration,\r\nIF(\r\n    DATEDIFF(t1.`end`, NOW()) < 0, \r\n    \"\", \r\n    (IF(\r\n        (IFNULL(prograss, 0) >= (DATEDIFF(NOW(), t1.`begin`) / DATEDIFF(t1.`end`, t1.`begin`) * 100) AND LEFT(t1.`end`, 4) != \'2059\') \r\n        OR LEFT(t1.`end`, 4) = \'2059\' ,\r\n        \"\",\r\n        \"\"\r\n    ))) AS \'status\',\r\nCONCAT(IFNULL(prograss, 0), \'%\') AS prograss\r\nFROM zt_project AS t1\r\nLEFT JOIN (\r\n    SELECT t22.project,\r\n    ROUND(IF(SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0)) > 0, SUM(t22.consumed) / (SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0))), 0) * 100, 2) AS prograss\r\n    FROM zt_project AS t21\r\n    LEFT JOIN zt_task AS t22 ON t21.id = t22.execution\r\n    WHERE t21.deleted = \'0\' AND t21.type IN (\'sprint\', \'kanban\')\r\n    AND t22.deleted = \'0\' AND t22.parent < 1\r\n    GROUP BY t22.project\r\n    UNION\r\n    SELECT  t.project, ROUND(SUM(t.prograss * (t.percent / 100)), 2) as prograss\r\n    FROM (\r\n        SELECT t21.id,t21.percent, t22.project,\r\n        IF(SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0)) > 0, ROUND(SUM(t22.consumed) / (SUM(t22.consumed) + SUM(IF(t22.status != \'closed\' && t22.status != \'cancel\', t22.`left`, 0))) * 1000 / 1000 * 100, 2), 0)  AS prograss\r\n        FROM zt_project AS t21\r\n        LEFT JOIN zt_task AS t22 ON t21.id = t22.execution\r\n        WHERE t21.deleted = \'0\' AND t21.type = \'stage\'\r\n        AND t22.deleted = \'0\' AND t22.parent < 1\r\n        AND t22.id IS NOT NULL\r\n        GROUP BY t21.id, t21.percent, t22.project\r\n    ) t\r\n    GROUP BY t.project\r\n) AS t2 ON t1.id = t2.project \r\nLEFT JOIN zt_project AS t3 ON SUBSTR(t1.path, 2, POSITION(\',\' IN SUBSTR(t1.path, 2)) -1) = t3.id AND t3.type = \'program\' AND t3.deleted = \'0\'\r\nWHERE t1.deleted = \'0\'\r\nAND t1.status = \'doing\' \r\nAND t1.type = \'project\'', 'published', 1, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10117, '-', 2, 'table', '84', '0', '', '{\"group\":[],\"column\":[{\"field\":\"project\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"begin\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"end\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"planDuration\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"realBegan\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"realDuration\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"prograss\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"status\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, '', '', 'SELECT id, name,project,`begin`, `end`, planDuration, IF(LEFT(realBegan, 4) = \'0000\', \'/\', realBegan) as realBegan, realDuration, CONCAT(prograss, \'%\') as prograss,\r\nIF(\r\n    DATEDIFF(`end`, NOW()) < 0,\r\n    \"\",\r\n    (IF(\r\n        prograss >= planPrograss,\r\n        \"\",\r\n        \"\"\r\n    ))\r\n) AS status\r\nFROM (\r\nSELECT t1.id,t1.name,t1.`begin`,t1.`end`,t1.`realBegan`,IFNULL(t3.name, \'/\') AS project,t3.id AS projectID,\r\nDATEDIFF(t1.`end`, t1.`begin`) + 1 AS planDuration, IF(DATEDIFF(t1.`end`, NOW()) >= 0, DATEDIFF(t1.`end`, NOW()) + 1, 0) AS realDuration,\r\nROUND(DATEDIFF(NOW(), t1.`begin`) / DATEDIFF(t1.`end`, t1.`begin`) * 100, 2) AS planPrograss,\r\nROUND(IF(SUM(t2.consumed) + SUM(IF(t2.status != \'closed\' && t2.status != \'cancel\', t2.`left`, 0)) > 0, SUM(t2.consumed) / (SUM(t2.consumed) + SUM(IF(t2.status != \'closed\' && t2.status != \'cancel\', t2.`left`, 0))), 0) * 100, 2) AS prograss\r\nFROM zt_project AS t1\r\nLEFT JOIN zt_task AS t2 ON t1.id = t2.execution\r\nLEFT JOIN zt_project AS t3 on t1.project = t3.id AND t3.type = \'project\' AND t3.deleted = \'0\'\r\nWHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status = \'doing\' AND t1.multiple = \'1\'\r\nAND t2.deleted = \'0\' AND t2.parent < 1\r\nGROUP BY t1.id\r\n) AS t\r\nORDER BY projectID ASC, id ASC', 'published', 1, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10118, '-', 2, 'table', '83', '0', '', '{\"group\":[],\"column\":[{\"field\":\"program\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"project\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"story\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"estimate\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"execution\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"workhour\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, '', '', 'SELECT\r\n  t1.id,\r\n  t1.name AS project,\r\n  IFNULL(t2.name, \'/\') AS program,\r\n  IFNULL(t3.story, 0) AS story,\r\n  IFNULL(t3.estimate, 0) AS estimate,\r\n  IFNULL(t4.execution, 0) AS execution,  \r\n  IFNULL(t5.workhour, 0) AS workhour\r\nFROM zt_project AS t1\r\nLEFT JOIN zt_project AS t2 ON FIND_IN_SET(t2.id, t1.path) AND t2.deleted = \'0\' AND t2.type = \'program\' AND t2.grade = 1\r\nLEFT JOIN (\r\n  SELECT t1.parent AS project, COUNT(1) AS story, ROUND(SUM(t1.estimate), 1) AS estimate\r\n  FROM (\r\n    SELECT DISTINCT t1.parent, t3.id, t3.estimate\r\n    FROM zt_project AS t1\r\n    LEFT JOIN zt_projectstory AS t2 ON t1.id = t2.project\r\n    LEFT JOIN zt_story AS t3 ON t2.story = t3.id AND t3.deleted = \'0\' AND t3.stage NOT IN (\'verified\', \'released\', \'closed\')\r\n    WHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t3.id IS NOT NULL\r\n  ) AS t1 GROUP BY project\r\n) AS t3 ON t1.id = t3.project\r\nLEFT JOIN (SELECT parent AS project, COUNT(1) AS execution FROM zt_project WHERE deleted = \'0\' AND type IN (\'sprint\', \'stage\', \'kanban\') AND status NOT IN (\'done\', \'closed\') GROUP BY project) AS t4 ON t1.id = t4.project\r\nLEFT JOIN (\r\n  SELECT t1.parent AS project, ROUND(SUM(t2.left), 1) AS workhour\r\n  FROM zt_project AS t1\r\n  LEFT JOIN zt_task AS t2 ON t1.id = t2.execution AND t2.deleted = \'0\' AND t2.parent < 1\r\n  WHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status NOT IN (\'done\', \'closed\') AND t2.id IS NOT NULL\r\n  GROUP BY project\r\n) AS t5 ON t1.id = t5.project\r\nWHERE t1.deleted = \'0\' AND t1.type = \'project\' AND t1.status = \'doing\'', 'published', 1, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10119, '-', 2, 'table', '83', '0', '', '{\"group\":[],\"column\":[{\"field\":\"project\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"execution\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"story\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"estimate\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"task\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"workhour\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, '', '', 'SELECT\r\n  t1.id,\r\n  t1.name AS execution,\r\n  IFNULL(t2.name, \'/\') AS project,\r\n  IFNULL(t3.story, 0) AS story,\r\n  IFNULL(t3.estimate, 0) AS estimate,\r\n  IFNULL(t4.task, 0) AS task,  \r\n  IFNULL(t4.workhour, 0) AS workhour\r\nFROM zt_project AS t1\r\nLEFT JOIN zt_project AS t2 ON t1.project = t2.id AND t2.type = \'project\'\r\nLEFT JOIN (\r\n  SELECT t1.id AS execution, COUNT(1) AS story, ROUND(SUM(t3.estimate), 1) AS estimate\r\n  FROM zt_project AS t1\r\n  LEFT JOIN zt_projectstory AS t2 ON t1.id = t2.project\r\n  LEFT JOIN zt_story AS t3 ON t2.story = t3.id AND t3.deleted = \'0\' AND t3.stage NOT IN (\'verified\', \'released\', \'closed\')\r\n  WHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status = \'doing\' AND t1.multiple = \'1\'\r\n  GROUP BY execution\r\n) AS t3 ON t1.id = t3.execution\r\nLEFT JOIN (\r\n  SELECT t1.id AS execution, SUM(IF(t2.status IN (\'wait\', \'doing\'), 1, 0)) AS task, ROUND(SUM(IF(t2.status IN (\'wait\', \'doing\', \'pause\'), t2.left, 0)), 1) AS workhour\r\n  FROM zt_project AS t1\r\n  LEFT JOIN zt_task AS t2 ON t1.id = t2.execution AND t2.deleted = \'0\' AND t2.parent < 1\r\n  WHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status = \'doing\' AND t1.multiple = \'1\'\r\n  GROUP BY execution\r\n) AS t4 ON t1.id = t4.execution\r\nWHERE t1.deleted = \'0\' AND t1.type IN (\'sprint\', \'stage\', \'kanban\') AND t1.status = \'doing\' AND t1.multiple = \'1\'\r\nORDER BY t2.id ASC, t1.id ASC', 'published', 1, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
