 IN (\'developed\',\'testing\',\'verfied\',\'released\') OR (zt_story.status=\'closed\' AND zt_story.closedReason=\'done\'))) t1 GROUP BY t1.storyid ORDER BY cases DESC) t2) t3', 'published', 0, '', 'system', '2022-12-07 14:59:41', '', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10208, '-', 3, 'waterpolo', '92', '0', '', '[{\"type\":\"waterpolo\",\"calc\":\"sum\",\"goal\":\"casedensity\",\"conditions\":[{\"field\":\"havecase\",\"condition\":\"eq\",\"value\":\"havecase\"}]}]', '[]', 0, '{\"casedensity\":{\"name\":\"casedensity\",\"object\":\"testcase\",\"field\":\"casedensity\",\"type\":\"number\"},\"havecase\":{\"name\":\"havecase\",\"object\":\"testcase\",\"field\":\"havecase\",\"type\":\"string\"}}', '{\"casedensity\":{\"zh-cn\":\"\\u7528\\u4f8b\\u5bc6\\u5ea6\",\"zh-tw\":\"\",\"en\":\"casedensity\",\"de\":\"\",\"fr\":\"\"},\"havecase\":{\"zh-cn\":\"\\u662f\\u5426\\u6709\\u7528\\u4f8b\",\"zh-tw\":\"\",\"en\":\"havecase\",\"de\":\"\",\"fr\":\"\"}}', 'SELECT ROUND(SUM(t2.cases)/SUM(t2.estimate),4) AS casedensity, \'havecase\' as havecase FROM (SELECT t1.storyid, t1.estimate, SUM(t1.iscase) cases FROM (SELECT zt_story.id storyid, zt_story.estimate, (CASE WHEN zt_case.id is null THEN 0 ELSE 1 END) iscase FROM zt_story LEFT JOIN zt_case ON zt_story.id=zt_case.story WHERE zt_story.deleted=\'0\' AND (zt_story.stage IN (\'developed\',\'testing\',\'verfied\',\'released\') OR (zt_story.status=\'closed\' AND zt_story.closedReason=\'done\'))) t1 GROUP BY t1.storyid, t1.estimate ORDER BY cases DESC) t2\nunion\nSELECT ROUND(1-SUM(t2.cases)/SUM(t2.estimate),4) AS casedensity, \'nocase\' as havecase FROM (SELECT t1.storyid, t1.estimate, SUM(t1.iscase) cases FROM (SELECT zt_story.id storyid, zt_story.estimate, (CASE WHEN zt_case.id is null THEN 0 ELSE 1 END) iscase FROM zt_story LEFT JOIN zt_case ON zt_story.id=zt_case.story WHERE zt_story.deleted=\'0\' AND (zt_story.stage IN (\'developed\',\'testing\',\'verfied\',\'released\') OR (zt_story.status=\'closed\' AND zt_story.closedReason=\'done\'))) t1 GROUP BY t1.storyid, t1.estimate ORDER BY cases DESC) t2', 'published', 0, '', 'system', '2022-12-07 14:59:41', '', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10209, '-Bug', 3, 'waterpolo', '91', '0', '', '[{\"type\":\"waterpolo\",\"calc\":\"sum\",\"goal\":\"bugdensity\",\"conditions\":[{\"field\":\"havebug\",\"condition\":\"eq\",\"value\":\"havebug\"}]}]', '[]', 0, '{\"bugdensity\":{\"name\":\"bugdensity\",\"object\":\"bug\",\"field\":\"bugdensity\",\"type\":\"number\"},\"havebug\":{\"name\":\"havebug\",\"object\":\"bug\",\"field\":\"havebug\",\"type\":\"string\"}}', '{\"bugdensity\":{\"zh-cn\":\"Bug\\u5bc6\\u5ea6\",\"zh-tw\":\"\",\"en\":\"bugdensity\",\"de\":\"\",\"fr\":\"\"},\"havebug\":{\"zh-cn\":\"\\u662f\\u5426\\u6709Bug\",\"zh-tw\":\"\",\"en\":\"havebug\",\"de\":\"\",\"fr\":\"\"}}', 'SELECT ROUND(SUM(t3.bug)/SUM(t3.estimate), 4) AS bugdensity, \'havebug\' as havebug FROM (SELECT t1.product product, IFNULL(t1.estimate,0) estimate, IFNULL(t2.bug,0) bug FROM (SELECT product, ROUND(SUM(estimate),2) estimate FROM zt_story WHERE deleted=\'0\' AND (stage IN (\'developed\',\'testing\',\'verfied\',\'released\') OR (status=\'closed\' AND closedReason=\'done\')) GROUP BY product) t1 LEFT JOIN (SELECT product, COUNT(id) bug FROM zt_bug WHERE deleted=\'0\' GROUP BY product) t2 ON t1.product=t2.product) t3\nunion\nSELECT ROUND(1-SUM(t3.bug)/SUM(t3.estimate), 4) AS bugdensity, \'nobug\' as havebug FROM (SELECT t1.product product, IFNULL(t1.estimate,0) estimate, IFNULL(t2.bug,0) bug FROM (SELECT product, ROUND(SUM(estimate),2) estimate FROM zt_story WHERE deleted=\'0\' AND (stage IN (\'developed\',\'testing\',\'verfied\',\'released\') OR (status=\'closed\' AND closedReason=\'done\')) GROUP BY product) t1 LEFT JOIN (SELECT product, COUNT(id) bug FROM zt_bug WHERE deleted=\'0\' GROUP BY product) t2 ON t1.product=t2.product) t3', 'published', 0, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10210, '-Bug', 3, 'waterpolo', '91', '0', '', '[{\"type\":\"waterpolo\",\"calc\":\"sum\",\"goal\":\"fixpercent\",\"conditions\":[{\"field\":\"havebug\",\"condition\":\"eq\",\"value\":\"havebug\"}]}]', '[]', 0, '{\"fixpercent\":{\"name\":\"fixpercent\",\"object\":\"bug\",\"field\":\"fixpercent\",\"type\":\"number\"},\"havebug\":{\"name\":\"havebug\",\"object\":\"bug\",\"field\":\"havebug\",\"type\":\"string\"}}', '{\"fixpercent\":{\"zh-cn\":\"Bug\\u4fee\\u590d\\u7387\",\"zh-tw\":\"\",\"en\":\"fixpercent\",\"de\":\"\",\"fr\":\"\"},\"havebug\":{\"zh-cn\":\"\\u662f\\u5426\\u6709Bug\",\"zh-tw\":\"\",\"en\":\"havebug\",\"de\":\"\",\"fr\":\"\"}}', 'SELECT ROUND(SUM(CASE WHEN resolution=\'fixed\' THEN 1 ELSE 0 END)/COUNT(id),4) AS fixpercent, \'havebug\' as havebug FROM zt_bug WHERE deleted = \'0\' \nunion\nSELECT ROUND(1-SUM(CASE WHEN resolution=\'fixed\' THEN 1 ELSE 0 END)/COUNT(id),4) AS fixpercent, \'nobug\' as havebug FROM zt_bug WHERE deleted = \'0\'', 'published', 0, '', 'system', '2022-12-07 14:59:41', 'admin', '2022-12-07 14:59:41', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10211, '-BugBugBug30', 3, 'cluBarX', '91', '0', '', '[{\"type\":\"cluBarX\",\"xaxis\":[{\"field\":\"\\u65e5\\u671f\",\"name\":\"\\u65e5\\u671f\",\"group\":\"day\"}],\"yaxis\":[{\"field\":\"Bug\\u603b\\u6570\",\"name\":\"Bug\\u603b\\u6570\",\"valOrAgg\":\"count\"},{\"field\":\"\\u6709\\u6548Bug\",\"name\":\"\\u6709\\u6548Bug\",\"valOrAgg\":\"sum\"},{\"field\":\"\\u5df2\\u89e3\\u51b3Bug\",\"name\":\"\\u5df2\\u89e3\\u51b3Bug\",\"valOrAgg\":\"sum\"}]}]', '[]', 4, '{\"Bug\\u603b\\u6570\":{\"name\":\"Bug\\u603b\\u6570\",\"object\":\"bug\",\"field\":\"Bug\\u603b\\u6570\",\"type\":\"number\"},\"\\u6709\\u6548Bug\":{\"name\":\"\\u6709\\u6548Bug\",\"object\":\"bug\",\"field\":\"\\u6709\\u6548Bug\",\"type\":\"string\"},\"\\u5df2\\u89e3\\u51b3Bug\":{\"name\":\"\\u5df2\\u89e3\\u51b3Bug\",\"object\":\"bug\",\"field\":\"\\u5df2\\u89e3\\u51b3Bug\",\"type\":\"string\"},\"\\u65e5\\u671f\":{\"name\":\"\\u65e5\\u671f\",\"object\":\"bug\",\"field\":\"\\u65e5\\u671f\",\"type\":\"date\"}}', '', 'select \nid \"Bug\",\n(case when  resolution in (\'fixed\',\'postponed\') or status=\'active\' then 1 else 0 end) \"Bug\",\n(case when  resolution=\'fixed\' then 1 else 0 end) \"Bug\",\nopenedDate \"\"\nfrom zt_bug\nwhere left(openedDate,10) > (select DATE_sub(MAX(NOW()), INTERVAL \'30\' DAY)) \nand left(openedDate,10) < NOW()\nand deleted=\'0\'', 'published', 1, '', 'system', '2023-04-06 12:51:56', 'admin', '2023-04-06 12:51:56', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10212, '-Bug', 3, 'line', '91', '0', '', '[{\"type\":\"line\",\"xaxis\":[{\"field\":\"year\",\"name\":\"year\",\"group\":\"\"}],\"yaxis\":[{\"field\":\"effectiveBugRate\",\"name\":\"effectiveBugRate\",\"valOrAgg\":\"sum\"}]}]', '[{\"field\":\"year\",\"type\":\"select\",\"name\":\"\\u5e74\\u4efd\"}]', 4, '{\"year\":{\"name\":\"year\",\"object\":\"bug\",\"field\":\"year\",\"type\":\"string\"},\"totalBugCount\":{\"name\":\"totalBugCount\",\"object\":\"bug\",\"field\":\"totalBugCount\",\"type\":\"string\"},\"effectiveBugCount\":{\"name\":\"effectiveBugCount\",\"object\":\"bug\",\"field\":\"effectiveBugCount\",\"type\":\"number\"},\"effectiveBugRate\":{\"name\":\"effectiveBugRate\",\"object\":\"bug\",\"field\":\"effectiveBugRate\",\"type\":\"number\"}}', '{\"year\":{\"zh-cn\":\"\\u5e74\\u4efd\",\"zh-tw\":\"\",\"en\":\"Year\",\"de\":\"\",\"fr\":\"\"},\"totalBugCount\":{\"zh-cn\":\"Bug\\u603b\\u6570\",\"zh-tw\":\"\",\"en\":\"Total Bug Count\",\"de\":\"\",\"fr\":\"\"},\"effectiveBugCount\":{\"zh-cn\":\"\\u6709\\u6548Bug\\u6570\",\"zh-tw\":\"\",\"en\":\"Effective Bug Count\",\"de\":\"\",\"fr\":\"\"},\"effectiveBugRate\":{\"zh-cn\":\"\\u6709\\u6548Bug\\u7387\",\"zh-tw\":\"\",\"en\":\"Effective Bug Rate\",\"de\":\"\",\"fr\":\"\"}}', 'select\nyear,\ncount(a.id) as totalBugCount,\nsum(a.effectivebug) as effectiveBugCount,\nsum(a.effectivebug)/count(a.id) effectiveBugRate\nfrom(\nselect \nleft(openedDate,4) year,\nid,\n(case when  resolution in (\'fixed\',\'postponed\') or status=\'active\' then 1 else 0 end) effectivebug,\n(case when  resolution=\'fixed\' then 1 else 0 end) fixedBug\nfrom zt_bug\nwhere zt_bug.deleted=\'0\'\n) a\ngroup by a.year\norder by  a.year', 'published', 0, '', 'system', '2023-04-06 13:00:04', 'admin', '2023-04-06 13:00:04', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (10213, '-Bug', 3, 'line', '91', '0', '', '[{\"type\":\"line\",\"xaxis\":[{\"field\":\"year\",\"name\":\"year\",\"group\":\"\"}],\"yaxis\":[{\"field\":\"bugCount\",\"name\":\"Bug\\u6570\",\"valOrAgg\":\"sum\"}]}]', '[{\"field\":\"year\",\"type\":\"select\",\"name\":\"\\u5e74\\u4efd\"}]', 4, '{\"year\":{\"name\":\"year\",\"object\":\"story\",\"field\":\"year\",\"type\":\"string\"},\"createdBugs\":{\"name\":\"createdBugs\",\"object\":\"story\",\"field\":\"createdBugs\",\"type\":\"string\"},\"exfixedstoryestimate\":{\"name\":\"exfixedstoryestimate\",\"object\":\"story\",\"field\":\"exfixedstoryestimate\",\"type\":\"number\"},\"bugCount\":{\"name\":\"Bug\\u6570\",\"object\":\"story\",\"field\":\"bugCount\",\"type\":\"number\"}}', '{\"year\":{\"zh-cn\":\"\\u5e74\\u4efd\",\"zh-tw\":\"\",\"en\":\"Year\",\"de\":\"\",\"fr\":\"\"},\"createdBugs\":{\"zh-cn\":\"\\u4ea7\\u751fBug\",\"zh-tw\":\"\",\"en\":\"Created Bug\",\"de\":\"\",\"fr\":\"\"},\"exfixedstoryestimate\":{\"zh-cn\":\"\\u5b8c\\u6210\\u9700\\u6c42\\u6570\",\"zh-tw\":\"\",\"en\":\"Finished Story\",\"de\":\"\",\"fr\":\"\"},\"bugCount\":{\"zh-cn\":\"\\u5355\\u4f4d\\u5b8c\\u6210\\u9700\\u6c42\\u89c4\\u6a21\\u4ea7\\u751f\\u7684Bug\\u6570\",\"zh-tw\":\"\",\"en\":\"Bug Density\",\"de\":\"\",\"fr\":\"\"}}', 'select\nbug.year as year,\ncreatedBugs, \nexfixedstoryestimate,\nround(createdBugs/exfixedstoryestimate,2) as bugCount                                                                                                                                                             \nfrom\n(select \nleft(openedDate,4) year,\ncount(id) createdBugs\nfrom zt_bug\nwhere zt_bug.deleted=\'0\'\ngroup by year\n) bug \nleft join\n(select\nsum(estimate) exfixedstoryestimate,\nleft(closedDate,4) year\nfrom\nzt_story\nwhere zt_story.deleted=\'0\' and zt_story.status=\'closed\' and zt_story.closedReason=\'done\'\ngroup by year\n) story\non story.year=bug.year\norder by bug.year', 'published', 0, '', 'system', '2023-04-06 13:05:10', 'admin', '2023-04-06 13:05:10', 0);
