INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (20010, '-', 1, 'table', '60', '0', ' ', '{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"createdTasks\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"contributors\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"finishedTasks\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, ' ', NULL, 'SELECT\n	t1.name,\n	t4.year,\n	t4.month,\n	t1.createdTasks,\n	t2.finishedTasks,\n	t3.contributors \nFROM\r\n  (\r\nselect distinct year(date) as year, month(date) as month\r\nfrom zt_action \r\n  ) as t4 \r\nleft join\n	(\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.openedDate ) AS YEAR,\n	MONTH ( t2.openedDate ) AS MONTH,\n	COUNT( t2.id ) AS createdTasks \nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project \nWHERE\n	t1.type = \'project\' \nGROUP BY\n	t1.id,\n	YEAR ( t2.openedDate ),\n	MONTH ( t2.openedDate ) \n	) AS t1 on t4.year = t1.year and t4.month = t1.month\n	LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.finishedDate ) AS YEAR,\n	MONTH ( t2.finishedDate ) AS MONTH,\n	COUNT( t2.id ) AS finishedTasks \nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project \nWHERE\n	t1.type = \'project\' \n	AND t2.finishedDate IS NOT NULL \nGROUP BY\n	t1.id,\n	YEAR ( t2.finishedDate ),\n	MONTH ( t2.finishedDate ) \n	) AS t2 ON t1.id = t2.id \n	AND t4.YEAR = t2.YEAR \n	AND t4.MONTH = t2.\n	MONTH LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t3.date ) AS YEAR,\n	MONTH ( t3.date ) AS MONTH,\n	COUNT( DISTINCT t3.actor ) AS CONTRIBUTORS \nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project\n	LEFT JOIN zt_action AS t3 ON t2.id = t3.objectID \nWHERE\n	t1.type = \'project\' \n	AND t3.objectType = \'task\' \n	AND t3.action IN ( \'opened\', \'closed\', \'finished\', \'canceled\', \'assigned\' ) \nGROUP BY\n	t1.id,\n	YEAR ( t3.date ),\n	MONTH ( t3.date ) \n	) AS t3 ON t1.id = t3.id \n	AND t4.YEAR = t3.YEAR \n	AND t4.MONTH = t3.MONTH\r\n	order by t1.id,t4.year', 'published', 1, ' ', 'system', '2023-08-16 13:37:24', 'admin', '2023-08-16 13:37:24', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (20011, '-', 1, 'table', '63', '0', ' ', '{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"createdCases\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"avgBugsOfCase\",\"valOrAgg\":\"value\",\"name\":\"Bug\"},\r\n{\"field\":\"createdBugs\",\"valOrAgg\":\"value\",\"name\":\"Bug\"},{\"field\":\"fixedBugs\",\"valOrAgg\":\"value\",\"name\":\"Bug\"},{\"field\":\"avgFixedCycle\",\"valOrAgg\":\"value\",\"name\":\"Bug\"}],\"filter\":[]}\r\n', '[]', 0, ' ', NULL, 'SELECT * FROM\r\n(\r\nSELECT\r\n	t1.name,\r\n	t6.year,\r\n	t6.month,\r\n	IFNULL(t5.createdCases, 0) AS createdCases,\r\n	IFNULL(t4.relativedBugs / t5.createdCases, 0) AS avgBugsOfCase,\r\n	IFNULL(t1.createdBugs, 0) AS createdBugs,\r\n	IFNULL(t2.fixedBugs, 0) AS fixedBugs,\r\n	IFNULL(t3.fixedCycle / t2.fixedBugs, 0) AS avgFixedCycle \r\nFROM\r\n	(\r\n	select distinct year(date) as year, month(date) as month\r\n	from zt_action\r\n	) AS t6 left join\r\n	(\r\n	SELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	YEAR ( t2.openedDate ) AS YEAR,\r\n	MONTH ( t2.openedDate ) AS MONTH,\r\n	COUNT( t2.id ) AS createdBugs \r\nFROM\r\n	zt_product AS t1\r\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\' \r\nGROUP BY\r\n	t1.id,\r\n	YEAR ( t2.openedDate ),\r\n	MONTH ( t2.openedDate )\r\n	) AS t1 on t1.year = t6.year AND t1.month = t6.month\r\n	LEFT JOIN (\r\nSELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	YEAR ( t2.resolvedDate ) AS YEAR,\r\n	MONTH ( t2.resolvedDate ) AS MONTH,\r\n	COUNT( t2.id ) AS fixedBugs \r\nFROM\r\n	zt_product AS t1\r\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\' \r\n	AND t2.`status` = \'closed\' \r\n	AND t2.resolution = \'fixed\' \r\nGROUP BY\r\n	t1.id,\r\n	YEAR ( t2.resolvedDate ),\r\n	MONTH ( t2.resolvedDate ) \r\n	) AS t2 ON t1.id = t2.id \r\n	AND t6.YEAR = t2.YEAR \r\n	AND t6.MONTH = t2.MONTH \r\n	LEFT JOIN (\r\nSELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	YEAR ( t2.resolvedDate ) AS YEAR,\r\n	MONTH ( t2.resolvedDate ) AS MONTH,\r\n	SUM( DATEDIFF( t2.resolvedDate, t2.openedDate ) ) AS fixedCycle \r\nFROM\r\n	zt_product AS t1\r\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\'\r\n	AND t2.`status` = \'closed\'\r\n	AND t2.resolution = \'fixed\' \r\nGROUP BY\r\n	t1.id,\r\n	YEAR ( t2.resolvedDate ),\r\n	MONTH ( t2.resolvedDate ) \r\n	) AS t3 ON t1.id = t3.id \r\n	AND t6.YEAR = t3.YEAR \r\n	AND t6.MONTH = t3.\r\n	MONTH LEFT JOIN (\r\nSELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	YEAR ( t2.openedDate ) AS YEAR,\r\n	MONTH ( t2.openedDate ) AS MONTH,\r\n	COUNT( t3.id ) AS relativedBugs \r\nFROM\r\n	zt_product AS t1\r\n	LEFT JOIN zt_case AS t2 ON t1.id = t2.product\r\n	LEFT JOIN zt_bug AS t3 ON t2.id = t3.`case` \r\nWHERE\r\n	t2.id IS NOT NULL \r\n	AND t3.id IS NOT NULL\r\n  AND t1.deleted = \'0\'\r\n	AND t2.deleted = \'0\'\r\n	AND t3.deleted = \'0\'\r\nGROUP BY\r\n	t1.id,\r\n	YEAR ( t2.openedDate ),\r\n	MONTH ( t2.openedDate ) \r\n	) AS t4 ON t1.id = t4.id \r\n	AND t6.YEAR = t4.YEAR \r\n	AND t6.MONTH = t4.\r\n	MONTH LEFT JOIN (\r\n SELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	YEAR ( t2.openedDate ) AS YEAR,\r\n	MONTH ( t2.openedDate ) AS MONTH,\r\n	COUNT( t2.id ) AS createdCases \r\nFROM\r\n	zt_product AS t1\r\n	LEFT JOIN zt_case AS t2 ON t1.id = t2.product \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\' \r\nGROUP BY\r\n	t1.id,\r\n	YEAR ( t2.openedDate ),\r\n	MONTH ( t2.openedDate )\r\n	) AS t5 ON t1.id = t5.id \r\n	AND t6.YEAR = t5.YEAR \r\n	AND t6.MONTH = t5.MONTH\r\n) AS t WHERE t.name IS NOT NULL', 'published', 1, ' ', 'system', '2023-08-16 13:37:24', 'admin', '2023-08-16 13:37:24', 0);
INSERT INTO `zt_chart`(`id`, `name`, `dimension`, `type`, `group`, `dataset`, `desc`, `settings`, `filters`, `step`, `fields`, `langs`, `sql`, `stage`, `builtin`, `objects`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `deleted`) VALUES (20012, '-', 1, 'table', '63', '0', ' ', '{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"createdStories\",\"valOrAgg\":\"value\",\"name\":\"\"},{\"field\":\"deliveredStories\",\"valOrAgg\":\"value\",\"name\":\"\"}],\"filter\":[]}', '[]', 0, ' ', NULL, 'SELECT * FROM\r\n(\r\nSELECT\r\n	t1.id,\r\n	t1.name,\r\n	t3.year,\r\n	t3.month,\r\n	IFNULL(t1.count, 0) AS createdStories,\r\n	IFNULL(t2.count, 0) AS deliveredStories \r\nFROM\r\n	(\r\n	select distinct year(date) as year, month(date) as month\r\n	from zt_action\r\n	)\r\n	as t3 left join\r\n	(\r\nSELECT\r\n	t2.id,\r\n	t2.NAME,\r\n	YEAR ( t1.openedDate ) AS YEAR,\r\n	MONTH ( t1.openedDate ) AS MONTH,\r\n	COUNT( t1.id ) AS count \r\nFROM\r\n	zt_story AS t1\r\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id \r\nWHERE\r\n	t2.deleted = \'0\'\r\n	AND t1.deleted = \'0\'\r\n	AND t1.type = \'story\'\r\nGROUP BY\r\n	t2.id,\r\n	YEAR,\r\nMONTH \r\n	) AS t1 on t3.year = t1.year and t3.month = t1.month\r\n	LEFT JOIN (\r\nSELECT\r\n	t1.id,\r\n	t1.NAME,\r\n	t1.YEAR,\r\n	t1.MONTH,\r\n	COUNT( distinct t1.story ) AS count \r\nFROM\r\n	(\r\nSELECT\r\n	t2.id,\r\n	t2.NAME, \r\n	YEAR ( t3.date ) AS YEAR,\r\n	MONTH ( t3.date ) AS MONTH,\r\n	t1.id AS story \r\nFROM\r\n	zt_story AS t1\r\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id\r\n	LEFT JOIN ( SELECT objectID, MAX( date ) AS date FROM zt_action WHERE objectType = \'story\' AND action = \'linked2release\' GROUP BY objectID ) AS t3 ON t1.id = t3.objectID \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\' \r\n	AND EXISTS ( SELECT 1 FROM zt_action WHERE objectID = t1.id AND objectType = \'story\' AND action = \'linked2release\' ) \r\nUNION\r\nSELECT\r\n	t2.id,\r\n	t2.NAME,\r\n	YEAR ( t1.closedDate ) AS YEAR,\r\n	MONTH ( t1.closedDate ) AS MONTH,\r\n	t1.id AS story \r\nFROM\r\n	zt_story AS t1\r\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id \r\nWHERE\r\n	t1.deleted = \'0\' \r\n	AND t2.deleted = \'0\' \r\n	AND t1.status = \'closed\' \r\n	AND t1.closedReason = \'done\' \r\n	) AS t1 \r\nGROUP BY\r\n	t1.id,\r\n	t1.name,\r\n	t1.YEAR,\r\n	t1.MONTH order by id asc \r\n	) AS t2 ON t1.id = t2.id \r\n	AND t3.YEAR = t2.YEAR \r\n	AND t3.MONTH = t2.MONTH\r\n) AS t\r\nWHERE t.name IS NOT NULL', 'published', 1, ' ', 'system', '2023-08-16 13:37:24', 'admin', '2023-08-16 13:37:24', 0);
